from gi.repository import GObject, Gio

class Document(GObject.Object):
    def __init__(self, uri=None):
        GObject.Object.__init__(self)
        self._file = None
        if uri:
            self.set_file(Gio.File(uri=uri))
    
    @GObject.property
    def file(self):
        return self._file
    
    @GObject.property
    def name(self):
        return self._name
        
    def set_file(self, gio_file):
        self._file = gio_file

    def set_name(self, name):
        self._name = name
        
        
