from gi.repository import GObject
from mykiss.logobject import LogObject
from mykiss.window import Window
    
class BasePlugin(GObject.Object, LogObject):
    """
    Base Plugin
    
    Abstract base class for all MyKiss plugins. Plugin authors should not use
    this class directly.
    """
    __gtype_name__ = 'MykissPlugin'
    
    def do_activate(self):
        raise NotImplementedError

    def do_deactivate(self):
        raise NotImplementedError

    def do_update_state(self):
        raise NotImplementedError

class WindowPlugin(BasePlugin):
    """
    Window Plugin
    
    A plugin activated for every Window instance.
    """
    __gtype_name__ = 'MykissWindowPlugin'
    window = GObject.property(type=Window)


